export const memoizeOnId = (fn) => {
    const hasOwnProperty = Object.prototype.hasOwnProperty;
    const results = {};
    const memoizedFn = (id) => {
        if (hasOwnProperty.call(results, id)) {
            return results[id];
        }
        return (results[id] = fn(id));
    };
    return memoizedFn;
};
